      return(self$toSimpleType())
    },

    #' @description
    #' Convert SimplifiedLocation to a base R type
    #'
    #' @return A base R type, e.g. a list or numeric/character array.
    toSimpleType = function() {
      SimplifiedLocationObject <- list()
      if (!is.null(self$`point`)) {
        SimplifiedLocationObject[["point"]] <-
          self$`point`$toSimpleType()
      }
      if (!is.null(self$`timezone`)) {
        SimplifiedLocationObject[["timezone"]] <-
          self$`timezone`
      }
      if (!is.null(self$`display_name`)) {
        SimplifiedLocationObject[["display_name"]] <-
          self$`display_name`
      }
      if (!is.null(self$`country`)) {
        SimplifiedLocationObject[["country"]] <-
          self$`country`$toSimpleType()
      }
      return(SimplifiedLocationObject)
    },

