    #' @description
    #' Validate JSON input with respect to SimplifiedLocation and throw an exception if invalid
    #'
    #' @param input the JSON input
    validateJSON = function(input) {
      input_json <- jsonlite::fromJSON(input)
      # check the required field `point`
      if (!is.null(input_json$`point`)) {
        stopifnot(R6::is.R6(input_json$`point`))
      } else {
        stop(paste("The JSON input `", input, "` is invalid for SimplifiedLocation: the required field `point` is missing."))
      }
      # check the required field `timezone`
      if (!is.null(input_json$`timezone`)) {
        if (!(is.character(input_json$`timezone`) && length(input_json$`timezone`) == 1)) {
          stop(paste("Error! Invalid data for `timezone`. Must be a string:", input_json$`timezone`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for SimplifiedLocation: the required field `timezone` is missing."))
      }
      # check the required field `display_name`
      if (!is.null(input_json$`display_name`)) {
        if (!(is.character(input_json$`display_name`) && length(input_json$`display_name`) == 1)) {
          stop(paste("Error! Invalid data for `display_name`. Must be a string:", input_json$`display_name`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for SimplifiedLocation: the required field `display_name` is missing."))
      }
      # check the required field `country`
      if (!is.null(input_json$`country`)) {
        stopifnot(R6::is.R6(input_json$`country`))
      } else {
        stop(paste("The JSON input `", input, "` is invalid for SimplifiedLocation: the required field `country` is missing."))
      }
    },

    #' @description
    #' To string (JSON format)
    #'
    #' @return String representation of SimplifiedLocation
    toString = function() {
      self$toJSONString()
    },

    #' @description
    #' Return true if the values in all fields are valid.
    #'
    #' @return true if the values in all fields are valid.
    isValid = function() {
      # check if the required `point` is null
      if (is.null(self$`point`)) {
        return(FALSE)
      }

      TRUE
    },

    #' @description
    #' Return a list of invalid fields (if any).
    #'
    #' @return A list of invalid fields (if any).
    getInvalidFields = function() {
      invalid_fields <- list()
      # check if the required `point` is null
      if (is.null(self$`point`)) {
        invalid_fields["point"] <- "Non-nullable required field `point` cannot be null."
      }

      invalid_fields
    },

    #' @description
    #' Print the object
    print = function() {
      print(jsonlite::prettify(self$toJSONString()))
      invisible(self)
    }
